mapper.select:
select *
from ex_relation
where account_name = #{accountName} and password = #{password};

select *
from student
where student_id = #{studentId} and password = #{password};

mapper.insert:

    select max(id) from administrator;

insert into administrator (admin_id, password, name, registration_id, birth, email, phone, address, id_photo_loc,center)
values (#{adminId}, #{password}, #{name}, #{registrationId}, #{birth}, #{email}, #{phone}, #{address}, #{idPhotoLoc}, #{center});
